const months={0:"Jan",1:"Feb",2:"Mar",3:"Apr",4:"Mei",5:"Jun",6:"Jul",7:"Aug",8:"Sept",9:"Okt",10:"Nov",11:"Dec"};
const nty = new Array(3);
const typ = new Array(3);
const dat = new Array(3);
const dte = new Array(3);
const dtu = new Array(3);
var newmsg = {"payload" : ""} ;
var d = new Date();
var dd= d.getFullYear()*10000 + (d.getMonth()+1)*100 + d.getDate();
var l = msg.payload.data.length;
var c = 0;
//node.warn("lenght is " + l +  " date is" + d);

for (var i = 0; i < l; i++){

var s = msg.payload.data[i].date; // yyyy-mm-dd
