var ss= new Date(su); // node red time stamp
//node.warn("date " + s +  " time" + ss);
var st = ss.getDate() + " " + months[ss.getMonth()];
var ds = s.replace('-',"");ds = ds.replace('-',""); // 2 times
var dt = parseInt(ds); // make int


if( dt>=dd )
    {
    nty[c] = msg.payload.data[i].nameType;
