    if (dt==dd ) dte[c]= "vandaag";
    else if (dt==dd+1 ) dte[c]= "morgen";
            else if (dt==dd+2 ) dte[c]= "overmorgen";

    c=c+1;
    }
if (c==3) {i = l} // mark end of loop
}

    newmsg.topic = "/nodered/sensor/AFV/state"    
    newmsg.payload = { 
